DROP TABLE IF EXISTS `attends`;

--
-- `attends` テーブルの作成
--
CREATE TABLE `attends` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT 'プライマリーキー（自動採番）',
  member_id int NOT NULL COMMENT 'メンバーID',
  attend_year int NOT NULL COMMENT '出席情報',
  note text COMMENT 'メモ',
  created datetime NOT NULL COMMENT '作成日時',
  modified datetime NOT NULL COMMENT '更新日時',   
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='出席情報テーブル';


